import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext'; // 🆕 AuthContext 추가 (로그인 상태 확인)
import { useNavigate } from "react-router-dom"; // 🆕 useNavigate 추가
import Sidebar from '../components/Sidebar';
import GanttChart from '../components/GanttChart';
import Chatbox from '../components/Chatbox';
import Dashboard from '../components/Dashboard';
import TaskBoard from '../components/TaskBoard';
import '../styles/MainPage.css';

const tasks = [
  { id: 1, name: 'Design Phase', status: 'Done', dueDate: '2025-01-30', assignee: 'Alice' },
  { id: 2, name: 'Development Phase', status: 'In Progress', dueDate: '2025-02-05', assignee: 'Bob' },
  { id: 3, name: 'Testing Phase', status: 'In Progress', dueDate: '2025-02-10', assignee: 'Charlie' },
];

const MainPage = () => {
  const { user, logout } = useContext(AuthContext); // 🆕 user와 logout 함수 가져오기
  const [filteredTasks, setFilteredTasks] = useState(tasks);
  const [menuOpen, setMenuOpen] = useState(false); // 🆕 드롭다운 상태 추가
  const navigate = useNavigate(); // 🆕 페이지 이동을 위한 useNavigate 추가

  // 🆕 로그인된 사용자는 자동으로 Dashboard로 이동
  useEffect(() => {
    if (user) {
      navigate("/dashboard");
    }
  }, [user, navigate]);

  const handleFilter = (filtered) => {
    setFilteredTasks(filtered);
  };

  return (
    <div className="main-page">
      {/* 🆕 상단 네비게이션 바 추가 */}
      <nav style={{ display: "flex", justifyContent: "space-between", padding: "10px" }}>
        <h2>Teamverse</h2>

        {user ? ( // 🆕 로그인 상태에 따라 버튼 다르게 표시
          <div style={{ position: "relative" }}>
            <button onClick={() => setMenuOpen(!menuOpen)}>
              {user.username} ▼
            </button>
            {menuOpen && (
              <div style={{
                position: "absolute",
                right: 0,
                top: "100%",
                background: "white",
                border: "1px solid gray",
                borderRadius: "5px",
                padding: "10px"
              }}>
                <p>{user.username}의 Teamverse</p>
                <button onClick={() => alert("설정 페이지 이동 예정")}>설정</button>
                <button onClick={logout}>로그아웃</button>
              </div>
            )}
          </div>
        ) : (
          <button onClick={() => (window.location.href = "/login")}>Login</button>
        )}
      </nav>


      {/* 기존 메인 페이지 UI */}
      <div className="content">
        <GanttChart tasks={filteredTasks} />
        <TaskBoard />  {/* 작업 보드 추가 */}
        <Dashboard tasks={filteredTasks} />
      </div>
      <div className="chatbox-container">
        <Sidebar />
        <Chatbox />
      </div>
    </div>
  );
};

export default MainPage;
