import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket"; // ✅ WebSocket 클라이언트 가져오기
import "../styles/InviteList.css"; 

const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [notifications, setNotifications] = useState([]);

  // ✅ 초대 목록 가져오기
  const fetchInvites = async () => {
    const token = getAccessToken();
    try {
      const response = await axios.get("http://localhost:8082/api/team/invites", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("📌 받은 초대 목록:", response.data);
      setInvites(response.data);
    } catch (error) {
      console.error("❌ 초대 목록 불러오기 실패:", error);
    }
  };

  useEffect(() => {
    fetchInvites();

    const stompClient = getStompClient();
    if (!stompClient.connected) {
      stompClient.onConnect = () => {
        console.log("✅ WebSocket 연결 성공 & 초대 알림 구독 시작");
        stompClient.subscribe("/topic/invites", (message) => {
          const newInvite = JSON.parse(message.body);
          console.log("📩 새 초대 알림 수신:", newInvite);
          setNotifications((prev) => [...prev, newInvite]);
        });
      };
      stompClient.activate();
    }

    return () => {
      console.log("🛑 WebSocket 해제");
      if (stompClient && stompClient.connected) {
        stompClient.deactivate();
      }
    };
  }, []);

  // ✅ 초대 수락 처리
  const handleAcceptInvite = async (inviteId) => {
    const token = getAccessToken();
    try {
      await axios.post(`http://localhost:8082/api/team/invites/${inviteId}/accept`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log(`✅ 초대(${inviteId}) 수락`);
      fetchInvites(); // 리스트 갱신
      refreshProjects(); // 프로젝트 목록 갱신
    } catch (error) {
      console.error("❌ 초대 수락 실패:", error);
    }
  };

  // ✅ 초대 거절 처리
  const handleRejectInvite = async (inviteId) => {
    const token = getAccessToken();
    try {
      await axios.post(`http://localhost:8082/api/team/invites/${inviteId}/reject`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log(`❌ 초대(${inviteId}) 거절`);
      fetchInvites();
    } catch (error) {
      console.error("❌ 초대 거절 실패:", error);
    }
  };

  return (
    <div className="invite-list-container">
      <h3>📩 초대 받은 목록</h3>

      {/* ✅ 초대 목록 표시 (PENDING 상태만) */}
      {invites.filter(invite => invite.status === "PENDING").length === 0 ? (
        <p>📭 대기 중인 초대가 없습니다.</p>
      ) : (
        invites
          .filter(invite => invite.status === "PENDING")
          .map((invite) => (
            <div key={invite.id} className="invite-card">
              <p>
                <strong>{invite.sender.username}</strong> 님이 <strong>{invite.project.name}</strong> 프로젝트에 초대했습니다.
              </p>
              <div className="invite-buttons">
                <button className="accept-btn" onClick={() => handleAcceptInvite(invite.id)}>✅ 수락</button>
                <button className="reject-btn" onClick={() => handleRejectInvite(invite.id)}>❌ 거절</button>
              </div>
            </div>
          ))
      )}

      {/* ✅ WebSocket을 통해 받은 초대 알림 */}
      {notifications.length > 0 && (
        <div className="invite-popup-container">
          {notifications.map((invite) => (
            <div key={invite.id} className="invite-popup">
              <p>
                <strong>{invite.sender.username}</strong> 님이 <strong>{invite.project.name}</strong> 프로젝트에 초대했습니다.
              </p>
              <button onClick={fetchInvites}>✅ 확인</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default InviteList;
