import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom"; // ‚úÖ useNavigate Ï∂îÍ∞Ä
import axios from "axios";
import { getAccessToken, refreshAccessToken } from "../utils/authUtils"; // ‚úÖ refreshToken ÌôúÏö©
import TaskModal from "../components/TaskModal"; // ‚úÖ Î™®Îã¨Ï∞Ω Ïª¥Ìè¨ÎÑåÌä∏ Ï∂îÍ∞Ä


const TaskPage = () => {
  const location = useLocation();
  const navigate = useNavigate(); // ‚úÖ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô Í∏∞Îä• Ï∂îÍ∞Ä
  const queryParams = new URLSearchParams(location.search);
  const projectId = queryParams.get("projectId");

  const [project, setProject] = useState(null); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Ï†ÄÏû•
  const [loading, setLoading] = useState(true); // ‚úÖ Î°úÎî© ÏÉÅÌÉú Ï∂îÍ∞Ä
  const [error, setError] = useState(null); // ‚úÖ ÏóêÎü¨ ÏÉÅÌÉú Ï∂îÍ∞Ä

  // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ìï®Ïàò
  const fetchProject = async (retry = true) => {
    if (!projectId) {
      setError("‚ùå ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä ÏóÜÏäµÎãàÎã§.");
      setLoading(false);
      return;
    }

    let token = getAccessToken(); // ‚úÖ ÌòÑÏû¨ ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞
    if (!token) {
      console.warn("‚ùå accessTokenÏù¥ ÏóÜÏäµÎãàÎã§. refreshTokenÏùÑ ÏÇ¨Ïö©ÌïòÏó¨ Ïû¨Î∞úÍ∏â ÏãúÎèÑ...");
      token = await refreshAccessToken(); // ‚úÖ ÏÉàÎ°úÏö¥ accessToken ÏöîÏ≤≠
      if (!token) {
        alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        navigate("/login"); // ‚úÖ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        return;
      }
    }

    try {
      console.log(`üì° ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ ÏöîÏ≤≠: /api/user/projects/${projectId}`);
      const response = await axios.get(`http://localhost:8082/api/user/projects/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
        withCredentials: true, // ‚úÖ Ïø†ÌÇ§ Ïù∏Ï¶ù Ìè¨Ìï®
      });

      console.log("‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ ÏùëÎãµ:", response.data);
      setProject(response.data);
      setLoading(false);
    } catch (error) {
      console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:", error);

      if (error.response?.status === 401 && retry) {
        console.warn("üîÑ accessTokenÏù¥ ÎßåÎ£åÎê®. refreshTokenÏúºÎ°ú Ïû¨ÏãúÎèÑ...");
        const newToken = await refreshAccessToken();
        if (newToken) {
          fetchProject(false); // ‚úÖ Ìïú Î≤àÎßå Ïû¨ÏãúÎèÑ
        } else {
          alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
          navigate("/login"); // ‚úÖ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        }
      } else {
        setError("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.");
        setLoading(false);
      }
    }
  };

  // ‚úÖ ÌéòÏù¥ÏßÄÍ∞Ä Î°úÎìúÎê† Îïå ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    fetchProject();
  }, [projectId]);

  return (
    <div>
      {loading ? (
        <h1>Î°úÎî© Ï§ë...</h1>
      ) : error ? (
        <h1>{error}</h1>
      ) : (
        <h1>{project ? project.name : "ÌîÑÎ°úÏ†ùÌä∏ ÏóÜÏùå"}</h1> // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ ÌëúÏãú
      )}
      {projectId && <p>ÌîÑÎ°úÏ†ùÌä∏ ID: {projectId}</p>}
    </div>
  );
};

export default TaskPage;
