  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // ‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î∂àÎü¨Ïò§Í∏∞ Ìï®Ïàò
  const fetchChatMessages = async () => {
    if (!projectId) {
      console.warn("‚ùå ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä Ï†ïÏùòÎêòÏßÄ ÏïäÏùå, Ï±ÑÌåÖ Î∂àÎü¨Ïò§ÏßÄ ÏïäÏùå.");
      return;
    }

    const token = getAccessToken();
    try {
      const response = await axios.get(`http://localhost:8082/api/chat/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
      console.log("‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î°úÎìú ÏÑ±Í≥µ:", response.data);
    } catch (error) {
      console.error("‚ùå Ï±ÑÌåÖ Í∏∞Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  useEffect(() => {
    if (projectId) { 
      fetchChatMessages();
    }
  }, [projectId]);

  // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä ÏóÜÏùÑ Í≤ΩÏö∞, Ï±ÑÌåÖ UI ÏûêÏ≤¥Î•º Î†åÎçîÎßÅÌïòÏßÄ ÏïäÏùå
  if (!projectId) {
    return <p className="chatbox-warning">üö® ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>;
  }

  return (
    <div className="chatbox-container">
      <h3>ÌåÄ Ï±ÑÌåÖ</h3>
      <div className="messages">
        {messages.length > 0 ? (
          messages.map((msg) => (
            <p key={msg.id}><strong>{msg.sender.username}</strong>: {msg.content}</p>
          ))
        ) : (
          <p>üì≠ ÏïÑÏßÅ Î©îÏãúÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
        )}
      </div>
      <input
        type="text"
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
        placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
      />
      <button>Ï†ÑÏÜ°</button>
    </div>
  );
};

export default Chatbox;
