import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import '../styles/Navbar.css';
import axios from "axios";


const Navbar = () => {
  const [invitations, setInvitations] = useState([]);

  useEffect(() => {
      const token = localStorage.getItem("accessToken");

      if (token) {
          axios.get("http://localhost:8082/api/team/invitations", {
              headers: { Authorization: `Bearer ${token}` },
          })
          .then(response => setInvitations(response.data))
          .catch(error => console.error("초대 알림 불러오기 실패:", error));
      }
  }, []);

  return (

    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>
      {invitations.length > 0 && (
                <div className="invitation-popup">
                    <p>새로운 팀 초대가 있습니다!</p>
                    <ul>
                        {invitations.map((inv) => (
                            <li key={inv.id}>{inv.email} 님이 초대하였습니다.</li>
                        ))}
                    </ul>
                </div>
            )}
      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>
        <Link to="/login" className="login-btn">Login</Link>
      </div>
    </nav>
  );
};

export default Navbar;
