import React, { useState, useEffect } from 'react';
import '../styles/Chatbox.css';
import FileManager from './FileManager';
import { Client } from "@stomp/stompjs";
import axios from "axios";

const Chatbox = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [stompClient, setStompClient] = useState(null);
  const accessToken = localStorage.getItem("accessToken"); // ✅ accessToken 상태 제거, 바로 사용하도록 변경

  useEffect(() => {
    console.log("✅ Chatbox 컴포넌트 마운트됨 - useEffect 실행");

    let accessToken = localStorage.getItem("accessToken");

    // ✅ 서버에서 검증할 수 없는 accessToken이면 삭제
    const validateToken = async () => {
      try {
        const response = await axios.get("http://localhost:8082/api/user", {
          headers: { Authorization: `Bearer ${accessToken}` },
          withCredentials: true,
        });
        if (response.status !== 200) {
          console.warn("⛔️ 서버에서 Access Token을 검증할 수 없음, 삭제 중...");
          localStorage.removeItem("accessToken"); // ✅ 잘못된 토큰 삭제
          accessToken = null;
        }
      } catch (error) {
        console.warn("⛔️ Access Token 검증 실패, 삭제 중...");
        localStorage.removeItem("accessToken"); // ✅ 잘못된 토큰 삭제
        accessToken = null;
      }
    };

    if (accessToken) {
      validateToken();
    }

    if (!accessToken) {
      console.log("⛔ 로그인하지 않은 상태에서는 Secondary Token을 요청할 수 없습니다.");
      return;
    }

    const connectWebSocket = async () => {
      try {
        console.log("🔍 현재 Access Token:", accessToken);

        const response = await axios.post("http://localhost:8082/api/user/secondary-token", {}, {
          headers: { Authorization: `Bearer ${accessToken}` },
          withCredentials: true,
        });

        const secondaryToken = response.data.secondaryToken;
        console.log("✅ Secondary Token:", secondaryToken);

        const client = new Client({
          brokerURL: `ws://localhost:8082/ws?secondaryToken=${secondaryToken}`,
          reconnectDelay: 5000,
          onConnect: (frame) => {
            console.log("🟢 WebSocket 연결 성공:", frame);
          },
          onStompError: (frame) => {
            console.error("⛔ WebSocket 연결 실패:", frame);
          },
        });

        client.activate();
        setStompClient(client);
      } catch (error) {
        console.error("❌ Secondary Token 요청 실패:", error);
      }
    };

    if (!stompClient) {
      connectWebSocket();
    }

    return () => {
      console.log("🔴 Chatbox 언마운트됨 - WebSocket 종료");
      if (stompClient) {
        stompClient.deactivate();
        console.log("🔴 WebSocket 연결 종료");
      }
    };
  }, [stompClient]);

  const handleSend = () => {
    if (input.trim() && stompClient) {
      const message = { user: "You", content: input, teamId: 1 };
      stompClient.publish({ destination: "/app/chat/send", body: JSON.stringify(message) });
      setInput('');
    }
  };

  return (
    <div className="chatbox">
      <h3>Team Chat</h3>
      <div className="messages">
        {messages.map((msg, index) => (
          <p key={index}>
            <strong>{msg.user}: </strong>{msg.content}
          </p>
        ))}
      </div>
      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message"
        />
        <button onClick={handleSend}>Send</button>
      </div>
      <FileManager />
    </div>
  );
};

export default Chatbox;
