import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";

const MainPage = () => {
    const [projects, setProjects] = useState(() => {
        return JSON.parse(localStorage.getItem("projects")) || []; // 🔹 저장된 프로젝트 불러오기
    });
    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");

    useEffect(() => {
        // ✅ 로그인한 사용자의 프로젝트 목록 불러오기
        const fetchProjects = async () => {
            const token = localStorage.getItem("token");
            if (!token) return;

            try {
                const response = await axios.get("http://localhost:8082/api/projects", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                console.log("✅ 프로젝트 목록 불러오기 성공:", response.data);
                setProjects(response.data);
                localStorage.setItem("projects", JSON.stringify(response.data)); // 저장
            } catch (error) {
                console.error("❌ 프로젝트 목록 불러오기 실패:", error);
            }
        };

        fetchProjects();
    }, []);

    // ✅ 새로운 프로젝트 생성 함수
    const handleCreateProject = async () => {
        const token = localStorage.getItem("token");

        if (!token) {
            alert("로그인이 필요합니다.");
            return;
        }

        if (!projectName.trim()) {
            alert("프로젝트 이름을 입력하세요.");
            return;
        }

        try {
            const response = await axios.post(
                "http://localhost:8082/api/projects",
                { name: projectName, startDate: new Date().toISOString().split("T")[0] },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "application/json",
                    },
                }
            );

            console.log("✅ 프로젝트 생성 성공:", response.data);
            const updatedProjects = [...projects, response.data];
            setProjects(updatedProjects);
            localStorage.setItem("projects", JSON.stringify(updatedProjects)); // 🔹 저장
            setShowModal(false);
            setProjectName(""); // 입력 필드 초기화
        } catch (error) {
            console.error("❌ 프로젝트 생성 실패:", error);
            alert("프로젝트 생성에 실패했습니다.");
        }
    };

    return (
        <div className="main-page">
            <div className="sidebar-container">
                <Sidebar />
            </div>

            <div className="content">
                {projects.length === 0 ? (
                    <div className="empty-gantt">
                        <p>현재 프로젝트가 없습니다.</p>
                        <button className="create-project-btn" onClick={() => setShowModal(true)}>
                            새로운 프로젝트 생성
                        </button>
                    </div>
                ) : (
                    <>
                        <h2 className="project-title">{projects[projects.length - 1].name}</h2>
                        <GanttChart tasks={[]} />
                    </>
                )}
                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>

            <div className="chatbox-container">
                <Chatbox />
            </div>

            {/* 🔹 프로젝트 생성 모달 */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>새 프로젝트 생성</h2>
                        <input 
                            type="text" 
                            placeholder="프로젝트 이름" 
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>생성</button>
                        <button onClick={() => setShowModal(false)}>취소</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
