import React, { useEffect } from 'react'; // useEffect Ï∂îÍ∞Ä
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import MainPage from './pages/MainPage';
import LoginPage from './pages/LoginPage';
import Navbar from './components/Navbar';
import StatisticsPage from './pages/StatisticsPage';
import TeamStatusPage from './pages/TeamStatusPage';
import { refreshToken } from './api/authApi'; // Refresh Token Í∞±Ïã† Ìï®Ïàò Ï∂îÍ∞Ä
import Dashboard from "./pages/Dashboard";
import Chatbox from "./components/Chatbox"; // ‚úÖ Chatbox Ï∂îÍ∞Ä




const tasks = [
  { id: 1, name: 'Design Phase', status: 'Done', dueDate: '2025-01-30', assignee: 'Alice' },
  { id: 2, name: 'Development Phase', status: 'In Progress', dueDate: '2025-02-05', assignee: 'Bob' },
  { id: 3, name: 'Testing Phase', status: 'In Progress', dueDate: '2025-02-10', assignee: 'Charlie' },
];

function App() {
  useEffect(() => {
    const interval = setInterval(async () => {
      const newAccessToken = await refreshToken(); // Refresh TokenÏùÑ Ïù¥Ïö©Ìïú Access Token Í∞±Ïã† ÏãúÎèÑ
      if (newAccessToken) {
        console.log("üü¢ ÏÉà ÌÜ†ÌÅ∞ Î∞úÍ∏â:", newAccessToken);
        document.cookie = `accessToken=${newAccessToken}; path=/;`; // ÏÉà Access TokenÏùÑ Ïø†ÌÇ§Ïóê Ï†ÄÏû•
      }
    }, 10 * 60 * 1000); // 10Î∂ÑÎßàÎã§ Ïã§Ìñâ (Access Token Í∞±Ïã†)

    return () => clearInterval(interval); // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ïñ∏ÎßàÏö¥Ìä∏Îê† Îïå interval Ï†úÍ±∞
  }, []);

  return (
    <Router>
      <div className="app">
        <Navbar />
        <Routes>
          <Route path="/" element={<MainPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/statistics" element={<StatisticsPage tasks={tasks} />} /> {/* tasks Ï†ÑÎã¨ */}
          <Route path="/team-status" element={<TeamStatusPage />} /> {/* ÌåÄ ÏÉÅÌÉú ÌéòÏù¥ÏßÄ Ï∂îÍ∞Ä */}
          <Route path="/dashboard" element={<Dashboard />}  /> {/* ÌåÄ ÏÉÅÌÉú ÌéòÏù¥ÏßÄ Ï∂îÍ∞Ä */}
          <Route path="/chat" element={<Chatbox />} /> {/* ‚úÖ Ï±ÑÌåÖ ÌéòÏù¥ÏßÄ Ï∂îÍ∞Ä */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
