import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import "../styles/InviteList.css"; // ✅ 스타일 적용

const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [notifications, setNotifications] = useState([]); // ✅ 팝업 알림 상태

  useEffect(() => {
    const fetchInvites = async () => {
      const token = getAccessToken();
      try {
        const response = await axios.get("http://localhost:8082/api/team/invites", {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("📌 받은 초대 목록:", response.data);
        setInvites(response.data);
      } catch (error) {
        console.error("❌ 초대 목록 불러오기 실패:", error);
      }
    };

    fetchInvites();

    // ✅ WebSocket 연결 (토큰 포함)
    const socket = new SockJS("http://localhost:8082/ws");
    const stompClient = new Client({
      webSocketFactory: () => socket,
      connectHeaders: {
        Authorization: `Bearer ${getAccessToken()}`, // ✅ WebSocket 요청에 인증 토큰 추가
      },
      debug: (str) => console.log("📌 STOMP Debug:", str),
      reconnectDelay: 5000,
      heartbeatIncoming: 4000,
      heartbeatOutgoing: 4000,
      onConnect: () => {
        console.log("✅ WebSocket 연결됨");
        stompClient.subscribe("/topic/invites", (message) => {
          const newInvite = JSON.parse(message.body);
          console.log("📩 새로운 초대 알림:", newInvite);
          setNotifications((prev) => [...prev, newInvite]); // ✅ 팝업 알림 추가
          fetchInvites(); // ✅ 초대 목록 새로고침
        });
      },
      onStompError: (frame) => {
        console.error("🚨 STOMP ERROR:", frame);
      },
    });

    stompClient.activate();

    return () => {
      stompClient.deactivate();
    };
  }, []);

  // ✅ 초대 수락 함수
  const acceptInvite = async (inviteId) => {
    const token = getAccessToken();
    try {
      await axios.post(`http://localhost:8082/api/team/invite/${inviteId}/accept`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("✅ 초대를 수락했습니다!");
      setInvites((prev) => prev.filter((invite) => invite.id !== inviteId));
      refreshProjects(); // ✅ 초대 수락 후 프로젝트 목록 갱신

      // ✅ 알림 제거 (3초 후 사라짐)
      setTimeout(() => {
        setNotifications((prev) => prev.filter((notif) => notif.id !== inviteId));
      }, 3000);

    } catch (error) {
      console.error("❌ 초대 수락 실패:", error);
    }
  };

  return (
    <>
      {/* ✅ 팝업 알림 창 */}
      <div className="invite-popup-container">
        {notifications.map((invite) => (
          <div key={invite.id} className="invite-popup">
            <p>
              <strong>{invite.sender.username}</strong> 님이 <strong>{invite.project.name}</strong> 프로젝트에 초대했습니다.
            </p>
            <button onClick={() => acceptInvite(invite.id)}>✅ 수락</button>
            <button className="reject-btn">❌ 거절</button>
          </div>
        ))}
      </div>
    </>
  );
};

export default InviteList;
