import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";

const MainPage = () => {
    const [projects, setProjects] = useState([]); // 로그인 후 프로젝트 없음
    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");

    useEffect(() => {
        setProjects([]); // 로그인 후 GanttChart는 비어있는 상태 유지
    }, []);

    const handleCreateProject = async () => {
        if (projectName.trim() === "") {
            alert("프로젝트 이름을 입력하세요.");
            return;
        }

        const token = localStorage.getItem("token"); // ✅ JWT 토큰 가져오기
        console.log("🔑 저장된 JWT 토큰:", token); // ✅ 토큰 확인 (디버깅)

        if (!token) {
            alert("로그인이 필요합니다.");
            return;
        }

        try {
            const requestData = {
                name: projectName,
                startDate: new Date().toISOString().split("T")[0],
            };

            console.log("📢 프로젝트 생성 요청 데이터:", requestData);

            const response = await axios.post(
                "http://localhost:8082/api/user/projects",
                requestData,
                {
                    headers: {
                        Authorization: `Bearer ${token}`, // ✅ 헤더에 JWT 토큰 포함
                        "Content-Type": "application/json", // ✅ JSON 형식 지정
                    },
                }
            );

            console.log("✅ 프로젝트 생성 성공:", response.data);

            setProjects([...projects, response.data]);
            setShowModal(false);
            setProjectName(""); // 입력 필드 초기화
        } catch (error) {
            console.error("❌ 프로젝트 생성 실패:", error);

            if (error.response) {
                console.error("❗ 서버 응답 데이터:", error.response.data);
                console.error("❗ HTTP 상태 코드:", error.response.status);

                if (error.response.status === 401) {
                    alert("인증이 필요합니다. 다시 로그인해 주세요.");
                } else if (error.response.status === 403) {
                    alert("권한이 없습니다. 관리자에게 문의하세요.");
                } else {
                    alert("프로젝트 생성에 실패했습니다.");
                }
            } else {
                alert("서버 연결에 문제가 있습니다.");
            }
        }
    };

    return (
        <div className="main-page">
            <div className="content">
                {projects.length === 0 ? (
                    <div className="empty-gantt">
                        <p>현재 프로젝트가 없습니다.</p>
                        <button className="create-project-btn" onClick={() => setShowModal(true)}>
                            새로운 프로젝트 생성
                        </button>
                    </div>
                ) : (
                    <GanttChart tasks={[]} />
                )}
                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>
            <div className="chatbox-container">
                <Sidebar />
                <Chatbox />
            </div>

            {/* 🔹 프로젝트 생성 모달 */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>새 프로젝트 생성</h2>
                        <input 
                            type="text" 
                            placeholder="프로젝트 이름" 
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>생성</button>
                        <button onClick={() => setShowModal(false)}>취소</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
