import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import TaskModal from "../components/TaskModal";

const TaskPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const projectId = queryParams.get("projectId");

  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editTask, setEditTask] = useState(null); //수정할 Task 저장

  // ✅ Task 목록 불러오기
  const fetchTasks = async () => {
    const token = getAccessToken();
    if (!token) {
      alert("로그인이 필요합니다.");
      navigate("/login");
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8082/api/user/tasks?projectId=${projectId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      setTasks(response.data);
    } catch (error) {
      console.error("❌ Task 목록 불러오기 실패:", error);
    }
  };

  // ✅ Task 추가 후 목록 갱신
  const refreshTasks = () => {
    fetchTasks();
  };

    // ✅ Task 삭제 함수 
    const handleDeleteTask = async (taskId) => {
      const token = getAccessToken();
      if (!token) {
        alert("로그인이 필요합니다.");
        return;
      }
  
      if (!window.confirm("정말로 이 업무를 삭제하시겠습니까?")) return;
  
      try {
        await axios.delete(`http://localhost:8082/api/user/tasks/${taskId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          withCredentials: true,
        });
  
        alert("업무가 성공적으로 삭제되었습니다.");
        refreshTasks(); // ✅ 삭제 후 목록 새로고침
      } catch (error) {
        console.error("❌ Task 삭제 실패:", error);
        alert("업무 삭제에 실패했습니다.");
      }
    };

  useEffect(() => {
    if (projectId) {
      fetchTasks();
    }
  }, [projectId]);

  return (
    <div>
      <h1>Task 관리</h1>
      <button onClick={() => { setEditTask(null); setIsModalOpen(true); }}>+ 업무 추가</button>

      {/* ✅ Task 목록 표시 */}
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <strong>{task.name}</strong> - {task.status} 
            <br /> 🗓 **시작일**: {task.startDate} | ⏳ **마감일**: {task.dueDate}  
            <button onClick={() => { setEditTask(task); setIsModalOpen(true); }}>수정</button> {/* ✅ 수정 버튼 추가 */}
            <button onClick={() => handleDeleteTask(task.id)} style={{ marginLeft: "10px", color: "red" }}>삭제</button> {/* ✅ 삭제 버튼 추가 */}
            </li>
        ))}
      </ul>

      {/* ✅ Task 추가 & 수정 모달 */}
      {isModalOpen && (
        <TaskModal
          onClose={() => setIsModalOpen(false)}
          projectId={projectId}
          refreshTasks={refreshTasks}
          editTask={editTask} // ✅ 수정할 Task 전달
        />
      )}
    </div>
  );
};

export default TaskPage;
