import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles/Navbar.css";

const Navbar = () => {
  const [invitations, setInvitations] = useState([]); 
  const [showPopup, setShowPopup] = useState(false);
  const [user, setUser] = useState(null);

  const fetchUserInfo = async () => {
    try {
        const token = localStorage.getItem("token");
        if (!token) {
            console.error("🚨 JWT 토큰이 없습니다! 로그인이 필요합니다.");
            return;
        }

        const response = await axios.get("http://localhost:8082/api/user", {
            headers: {
                Authorization: `Bearer ${token}`, // ✅ JWT 토큰 추가
                "Content-Type": "application/json",
            },
            withCredentials: true, // ✅ JWT 포함 요청
        });

        console.log("✅ 사용자 정보 불러오기 성공:", response.data);
        localStorage.setItem("user", JSON.stringify(response.data));
        setUser(response.data);  // ✅ 사용자 상태 업데이트
    } catch (error) {
        console.error("❌ 사용자 정보 불러오기 실패:", error);
    }
};


// useEffect 내부에서 실행
useEffect(() => {
    fetchUserInfo();
}, []);


  // ✅ 초대 목록 불러오기
  const fetchInvitations = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      const response = await axios.get("http://localhost:8082/api/team/invitations", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setInvitations(response.data);
      if (response.data.length > 0) setShowPopup(true);
    } catch (error) {
      console.error("초대 목록 불러오기 실패:", error);
    }
  };

  // ✅ 로그아웃 함수
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("refreshToken");
    localStorage.removeItem("user");
    setUser(null); // ✅ 상태 업데이트하여 UI 변경
    window.location.href = "/login"; // 로그인 페이지로 이동
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>

      {/* ✅ 초대 팝업 */}
      {showPopup && (
        <div className="invitation-popup">
          <h2>초대 알림</h2>
          <ul>
            {invitations.map((invite) => (
              <li key={invite.id}>
                <p>{invite.email}님이 팀 초대를 보냈습니다.</p>

              </li>
            ))}
          </ul>
          <button className="close-btn" onClick={() => setShowPopup(false)}>닫기</button>
        </div>
      )}

      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>
        {user ? (
          <div className="user-info">
            <span>Welcome, {user.username}!</span>
            <button onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <Link to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
