import React, { useState, useEffect } from 'react';
import '../styles/Chatbox.css';
import FileManager from './FileManager';
import connectToChat from '../utils/ConnectToChat';
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs"; // ✅ 올바른 임포트
import axios from "axios";

const Chatbox = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [stompClient, setStompClient] = useState(null);

  useEffect(() => {
    const connectWebSocket = async () => {
      try {
        // 1️⃣ Secondary Token 요청
        const response = await axios.post("http://localhost:8082/api/user/secondary-token", {}, { withCredentials: true });
        const secondaryToken = response.data.secondaryToken;
        console.log("✅ Secondary Token:", secondaryToken);

        // 2️⃣ WebSocket 연결 (Secondary Token 포함)
        const socket = new SockJS(`http://localhost:8082/ws?secondaryToken=${secondaryToken}`);
        const client = Stomp.over(socket);

        client.connect({}, (frame) => {
          console.log("🟢 WebSocket 연결 성공:", frame);

          // 3️⃣ 채팅방 구독 (teamId = 1 예시)
          client.subscribe("/topic/chat/1", (message) => {
            const receivedMessage = JSON.parse(message.body);
            setMessages((prevMessages) => [...prevMessages, receivedMessage]);
          });

          setStompClient(client);
        }, (error) => {
          console.error("⛔ WebSocket 연결 실패:", error);
        });

      } catch (error) {
        console.error("❌ Secondary Token 요청 실패:", error);
      }
    };

    connectWebSocket();

    return () => {
      if (stompClient) {
        stompClient.disconnect();
        console.log("🔴 WebSocket 연결 종료");
      }
    };
  }, []);

  const handleSend = () => {
    if (input.trim() && stompClient) {
      const message = { user: "You", content: input, teamId: 1 }; // 팀 ID는 1로 가정
      stompClient.send("/app/chat/send", {}, JSON.stringify(message));
      setInput('');
    }
  };

  return (
    <div className="chatbox">
      <h3>Team Chat</h3>
      <div className="messages">
        {messages.map((msg, index) => (
          <p key={index}>
            <strong>{msg.user}: </strong>{msg.content}
          </p>
        ))}
      </div>
      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message"
        />
        <button onClick={handleSend}>Send</button>
      </div>
      <FileManager /> {/* 파일 관리 기능 추가 */}
    </div>
  );
};

export default Chatbox;
