import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket"; // ‚úÖ WebSocket ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞
import "../styles/InviteList.css"; 

const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [stompClient, setStompClient] = useState(null); // ‚úÖ WebSocket ÏÉÅÌÉú Í¥ÄÎ¶¨

  useEffect(() => {
    const fetchInvites = async () => {
        const token = getAccessToken();
        try {
            const response = await axios.get("http://localhost:8082/api/team/invites", {
                headers: { Authorization: `Bearer ${token}` },
            });
            console.log("üìå Î∞õÏùÄ Ï¥àÎåÄ Î™©Î°ù:", response.data);
            setInvites(response.data);
        } catch (error) {
            console.error("‚ùå Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
        }
    };

    fetchInvites();

    // ‚úÖ WebSocket ÏÑ§Ï†ï
    const client = getStompClient();
    setStompClient(client); // ‚úÖ ÏÉÅÌÉú Ï†ÄÏû•

    // ‚úÖ WebSocket Ïó∞Í≤∞ ÏÉÅÌÉú ÌôïÏù∏ ÌõÑ ÌôúÏÑ±Ìôî
    if (!client.active) {
        console.log("üü¢ WebSocket ÌôúÏÑ±Ìôî Ï§ë...");
        client.activate();
    }

    const subscribeToInvites = () => {
        console.log("‚úÖ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ & Ï¥àÎåÄ ÏïåÎ¶º Íµ¨ÎèÖ ÏãúÏûë");
        client.subscribe("/topic/invites", (message) => {
            const newInvite = JSON.parse(message.body);
            console.log("üì© ÏÉà Ï¥àÎåÄ ÏïåÎ¶º ÏàòÏã†:", newInvite);
            setNotifications((prev) => [...prev, newInvite]);
        });
    };

    // ‚úÖ WebSocket Ïó∞Í≤∞ ÌõÑ Íµ¨ÎèÖ
    if (client.connected) {
        subscribeToInvites();
    } else {
        client.onConnect = subscribeToInvites;
    }

    return () => {
        console.log("üõë WebSocket Ìï¥Ï†ú");
        if (client.connected) {
            client.deactivate();
        }
    };
  }, []);

  return (
    <>
        <div className="invite-popup-container">
            {notifications.length > 0 && notifications.map((invite) => (
                <div key={invite.id} className="invite-popup">
                    <p>
                        <strong>{invite.sender.username}</strong> ÎãòÏù¥ <strong>{invite.project.name}</strong> ÌîÑÎ°úÏ†ùÌä∏Ïóê Ï¥àÎåÄÌñàÏäµÎãàÎã§.
                    </p>
                    <button onClick={() => window.location.reload()}>‚úÖ ÌôïÏù∏</button>
                </div>
            ))}
        </div>
    </>
  );
};

export default InviteList;
