import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { stompClient } from "../api/websocket"; // ‚úÖ WebSocket ÏÑ§Ï†ï Î∂àÎü¨Ïò§Í∏∞
import "../styles/InviteList.css"; // ‚úÖ Ïä§ÌÉÄÏùº Ï†ÅÏö©

const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [notifications, setNotifications] = useState([]); // ‚úÖ ÌåùÏóÖ ÏïåÎ¶º ÏÉÅÌÉú

  useEffect(() => {
    const fetchInvites = async () => {
      const token = getAccessToken();
      try {
        const response = await axios.get("http://localhost:8082/api/team/invites", {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("üìå Î∞õÏùÄ Ï¥àÎåÄ Î™©Î°ù:", response.data);
        setInvites(response.data);
      } catch (error) {
        console.error("‚ùå Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchInvites();

    // ‚úÖ WebSocket Î©îÏãúÏßÄ ÏàòÏã† Ï≤òÎ¶¨
    const subscribeToInvites = () => {
      if (stompClient.connected) {
        stompClient.subscribe("/topic/invites", (message) => {
          const newInvite = JSON.parse(message.body);
          console.log("üì© ÏÉà Ï¥àÎåÄ ÏïåÎ¶º:", newInvite);
          setNotifications((prev) => [...prev, newInvite]);
          setInvites((prev) => [...prev, newInvite]);
        });
      } else {
        console.warn("üö® STOMPÍ∞Ä ÏïÑÏßÅ Ïó∞Í≤∞ÎêòÏßÄ ÏïäÏùå!");
      }
    };

    // WebSocket Ïó∞Í≤∞ ÌõÑ Íµ¨ÎèÖ Ïã§Ìñâ
    if (stompClient.connected) {
      subscribeToInvites();
    } else {
      stompClient.onConnect = subscribeToInvites;
    }

    return () => {
      stompClient.deactivate(); // ‚úÖ WebSocket Ïó∞Í≤∞ Ìï¥Ï†ú
    };
  }, []);

  return (
    <>
      {/* ‚úÖ ÌåùÏóÖ ÏïåÎ¶º Ï∞Ω */}
      <div className="invite-popup-container">
        {notifications.map((invite) => (
          <div key={invite.id} className="invite-popup">
            <p>
              <strong>{invite.sender.username}</strong> ÎãòÏù¥ <strong>{invite.project.name}</strong> ÌîÑÎ°úÏ†ùÌä∏Ïóê Ï¥àÎåÄÌñàÏäµÎãàÎã§.
            </p>
            <button onClick={() => window.location.reload()}>‚úÖ ÌôïÏù∏</button>
          </div>
        ))}
      </div>
    </>
  );
};

export default InviteList;
