import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles/Navbar.css";

const Navbar = () => {
  const [invitations, setInvitations] = useState([]); 
  const [showPopup, setShowPopup] = useState(false);
  const [user, setUser] = useState(null);

  // ✅ 로그인 후 사용자 정보 불러오기
  useEffect(() => {
    const fetchUserInfo = () => {
      const storedUser = localStorage.getItem("user");
      if (storedUser) {
        setUser(JSON.parse(storedUser));
      }
    };

    fetchUserInfo(); // 초기 로드 시 실행

    // ✅ 로그인 상태가 변경될 때마다 user 상태 업데이트
    const handleStorageChange = () => {
      fetchUserInfo();
    };

    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  // ✅ 초대 목록 불러오기
  const fetchInvitations = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      const response = await axios.get("http://localhost:8082/api/team/invitations", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setInvitations(response.data);
      if (response.data.length > 0) setShowPopup(true);
    } catch (error) {
      console.error("초대 목록 불러오기 실패:", error);
    }
  };

  // ✅ 로그아웃 함수
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("refreshToken");
    localStorage.removeItem("user");
    setUser(null); // ✅ 상태 업데이트하여 UI 변경
    window.location.href = "/login"; // 로그인 페이지로 이동
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>

      {/* ✅ 초대 팝업 */}
      {showPopup && (
        <div className="invitation-popup">
          <h2>초대 알림</h2>
          <ul>
            {invitations.map((invite) => (
              <li key={invite.id}>
                <p>{invite.email}님이 팀 초대를 보냈습니다.</p>
                <button className="accept-btn" onClick={() => acceptInvitation(invite.id)}>수락</button>
                <button className="reject-btn" onClick={() => rejectInvitation(invite.id)}>거절</button>
              </li>
            ))}
          </ul>
          <button className="close-btn" onClick={() => setShowPopup(false)}>닫기</button>
        </div>
      )}

      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>
        {user ? (
          <div className="user-info">
            <span>Welcome, {user.username}!</span>
            <button onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <Link to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
