import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import '../styles/Navbar.css';
import axios from "axios";


const Navbar = () => {

  const [invitations, setInvitations] = useState([]);  // Ï¥àÎåÄ Î™©Î°ù
  const [showPopup, setShowPopup] = useState(false);  // ÌåùÏóÖ ÏÉÅÌÉú

  useEffect(() => {
    const token = localStorage.getItem("token"); // üîπ Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞
    axios.get("http://localhost:8082/api/team/invitations", {
      headers: { Authorization: `Bearer ${token}` }, // üî• JWT ÌÜ†ÌÅ∞ Ï∂îÍ∞Ä
      withCredentials: true,
    })
    .then(response => {
      setInvitations(response.data);
      if (response.data.length > 0) {
        setShowPopup(true);
      }
    })
    .catch(error => console.error("Ï¥àÎåÄ ÏïåÎ¶º Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error));
  }, []);

  return (

    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>
      {showPopup && (
        <div className="invitation-popup">
          <h2>Ï¥àÎåÄ ÏïåÎ¶º</h2>
          <ul>
            {invitations.map((invite, index) => (
              <li key={index}>{invite.email}ÎãòÏù¥ Ï¥àÎåÄÌñàÏäµÎãàÎã§.</li>
            ))}
          </ul>
          <button onClick={() => setShowPopup(false)}>Îã´Í∏∞</button>
        </div>
      )}
      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>
        <Link to="/login" className="login-btn">Login</Link>
      </div>
    </nav>
  );
};

export default Navbar;
