import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles/Navbar.css";

const Navbar = () => {
  const [invitations, setInvitations] = useState([]); 
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    fetchInvitations();
}, []); // ✅ 빈 배열을 유지하여 초기에 한 번만 실행



  // ✅ 초대 수락 기능
  const acceptInvitation = async (invitationId) => {
    try {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("로그인이 필요합니다.");
            return;
        }

        await axios.post(
            `http://localhost:8082/api/team/accept-invitation/${invitationId}`,
            {},
            {
                headers: {
                    Authorization: `Bearer ${token}`,  // ✅ JWT 토큰 추가
                },
            }
        );

        alert("초대를 수락하였습니다!");
        setInvitations((prev) => prev.filter((invite) => invite.id !== invitationId));
    } catch (error) {
        console.error("초대 수락 실패:", error);
    }
};

  // ✅ 초대 거절 기능
  const rejectInvitation = async (invitationId) => {
    try {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("로그인이 필요합니다.");
            return;
        }

        await axios.post(
            `http://localhost:8082/api/team/reject-invitation/${invitationId}`,
            {},
            {
                headers: {
                    Authorization: `Bearer ${token}`,  // ✅ JWT 토큰 추가
                },
            }
        );

        alert("초대를 거절하였습니다.");
        setInvitations((prev) => prev.filter((invite) => invite.id !== invitationId));
    } catch (error) {
        console.error("초대 거절 실패:", error);
    }
};


  const closePopup = () => setShowPopup(false);

  const fetchInvitations = async () => {
    try {
        const token = localStorage.getItem("token");
        if (!token) {
            console.error("🚨 JWT 토큰이 없습니다! 로그인이 필요합니다.");
            return;
        }

        const response = await axios.get("http://localhost:8082/api/team/invitations", {
            headers: {
                Authorization: `Bearer ${token}`,  // ✅ JWT 토큰 추가
                "Content-Type": "application/json",
            },
        });

        console.log("✅ 초대 목록 불러오기 성공:", response.data);
        setInvitations(response.data);  // ✅ 초대 목록 업데이트 (누락된 부분)
        if (response.data.length > 0) {
            setShowPopup(true);  // ✅ 초대가 있으면 팝업 표시
        }
    } catch (error) {
        console.error("❌ 초대 목록 불러오기 실패:", error);
    }
};


  return (
    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>

      {/* ✅ 초대 팝업 */}
      {showPopup && (
        <div className="invitation-popup">
          <h2>초대 알림</h2>
          <ul>
            {invitations.map((invite) => (
              <li key={invite.id}>
                <p>{invite.email}님이 팀 초대를 보냈습니다.</p>
                <button className="accept-btn" onClick={() => acceptInvitation(invite.id)}>수락</button>
                <button className="reject-btn" onClick={() => rejectInvitation(invite.id)}>거절</button>
              </li>
            ))}
          </ul>
          <button className="close-btn" onClick={closePopup}>닫기</button>
        </div>
      )}

      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>
        <Link to="/login" className="login-btn">Login</Link>
      </div>
    </nav>
  );
};

export default Navbar;
