import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";

const MainPage = () => {
    const [projects, setProjects] = useState([]); // 로그인 후 프로젝트 없음
    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");

    useEffect(() => {
        setProjects([]); // 로그인 후 GanttChart는 비어있는 상태 유지
    }, []);

    const handleCreateProject = async () => {
      if (projectName.trim() === "") {
          alert("프로젝트 이름을 입력하세요.");
          return;
      }

      try {
          const response = await axios.post("http://localhost:8082/api/projects", { name: projectName });
          const newProject = response.data; // 백엔드에서 반환한 프로젝트 데이터

          setProjects([...projects, newProject]); // 기존 프로젝트 목록에 추가
          setShowModal(false);
          setProjectName(""); // 입력 필드 초기화
      } catch (error) {
          console.error("프로젝트 생성 실패:", error);
          alert("프로젝트 생성에 실패했습니다.");
      }
  };

    return (
        <div className="main-page">
            <div className="content">
                {projects.length === 0 ? (
                    <div className="empty-gantt">
                        <p>현재 프로젝트가 없습니다.</p>
                        <button className="create-project-btn" onClick={() => setShowModal(true)}>
                            새로운 프로젝트 생성
                        </button>
                    </div>
                ) : (
                    <GanttChart tasks={[]} />
                )}
                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>
            <div className="chatbox-container">
                <Sidebar />
                <Chatbox />
            </div>

            {/* 🔹 프로젝트 생성 모달 */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>새 프로젝트 생성</h2>
                        <input 
                            type="text" 
                            placeholder="프로젝트 이름" 
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>생성</button>
                        <button onClick={() => setShowModal(false)}>취소</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
