import React, { useEffect, useContext } from 'react';
import { Router, Routes, Route } from 'react-router-dom';
import MainPage from './pages/MainPage';
import LoginPage from './pages/LoginPage';
import Dashboard from './pages/Dashboard'; // 🆕 Dashboard 페이지 추가
import Navbar from './components/Navbar';
import StatisticsPage from './pages/StatisticsPage';
import TeamStatusPage from './pages/TeamStatusPage';
import ProtectedRoute from './components/ProtectedRoute'; // 🆕 로그인한 사용자만 접근 가능하도록 보호
import { refreshToken } from './api/authApi'; // 🆕 Refresh Token을 이용한 Access Token 갱신 함수 추가
import { AuthContext } from './context/AuthContext'; // 🆕 로그인 상태 관리를 위한 AuthContext 추가

function App() {
  const { user, logout } = useContext(AuthContext); // 🆕 현재 로그인된 사용자 정보와 로그아웃 함수 가져오기

  useEffect(() => {
    // 🆕 10분마다 Access Token 자동 갱신
    const interval = setInterval(async () => {
      try {
        const newAccessToken = await refreshToken(); // Refresh Token을 이용하여 Access Token 갱신 시도
        if (newAccessToken) {
          console.log("🟢 새 토큰 발급:", newAccessToken);
          localStorage.setItem("accessToken", newAccessToken); // 🆕 새로운 Access Token을 localStorage에 저장
        } else {
          console.warn("🔴 토큰 갱신 실패: 로그아웃 처리");
          logout(); // 🆕 토큰 갱신 실패 시 자동 로그아웃
        }
      } catch (error) {
        console.error("🔴 토큰 갱신 중 오류 발생:", error);
        logout(); // 🆕 오류 발생 시 자동 로그아웃
      }
    }, 10 * 60 * 1000); // 🆕 10분(600,000ms)마다 실행

    return () => clearInterval(interval); // 🆕 컴포넌트가 언마운트될 때 interval 제거
  }, [logout]); // 🆕 로그아웃 함수가 변경될 경우 useEffect 재실행

  return (
    <Router>
      <div className="app">
        <Navbar />
        <Routes>
          <Route path="/" element={<MainPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/dashboard" element={
            <ProtectedRoute> {/* 🆕 로그인한 사용자만 Dashboard 접근 가능하도록 보호 */}
              <Dashboard />
            </ProtectedRoute>
          } />
          <Route path="/statistics" element={<StatisticsPage />} /> {/* 🆕 StatisticsPage로 이동하는 경로 설정 */}
          <Route path="/team-status" element={<TeamStatusPage />} /> {/* 🆕 TeamStatusPage로 이동하는 경로 설정 */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
