import axios from "axios";

const API_BASE_URL = "http://localhost:8082"; // ✅ 백엔드 서버 URL 수정

export const signup = async (name, email, password) => {
    try {
        // ✅ API 경로를 `/register`로 변경
        const response = await axios.post(`${API_BASE_URL}/register`, { 
            username: name, // ✅ 백엔드에서 username 필드 사용
            email, 
            password 
        }, { withCredentials: true }); // ✅ 쿠키 저장 활성화 (자동 로그인 대비)

        return response.data;
    } catch (error) {
        return { message: error.response?.data?.message || "회원가입 실패" };
    }
};

export const login = async (email, password) => {
    try {
        const response = await axios.post("http://localhost:8082/api/auth/login", {
            email,
            password
        });

        return response.data;
    } catch (error) {
        return { message: error.response?.data?.message || "로그인 실패" };
    }
};
