import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import "../styles/Sidebar.css";

const Sidebar = ({ projectId }) => {
  const [teamMembers, setTeamMembers] = useState([]);

  const fetchTeamMembers = async () => {
    if (!projectId) return;

    const token = getAccessToken();
    try {
      const response = await axios.get(`http://localhost:8082/api/projects/${projectId}/team-members`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTeamMembers(response.data);
    } catch (error) {
      console.error("‚ùå ÌåÄÏõê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  useEffect(() => {
    fetchTeamMembers();
  }, [projectId]);

  return (
    <div className="sidebar">
      <h3>üë• ÌåÄ Î©§Î≤Ñ</h3>
      {teamMembers.length === 0 ? (
        <p>ÌòÑÏû¨ ÌåÄÏõêÏù¥ ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {teamMembers.map((member) => (
            <li key={member.id}>
              <strong>{member.username}</strong> <span>({member.role})</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Sidebar;
