import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { logoutUser } from "../api/authApi";
import "../styles/Navbar.css";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // ✅ 로그인 상태를 useState로 관리

  // useEffect에서 로그인 상태를 지속적으로 확인
  const checkLoginStatus = () => {
    const cookies = document.cookie.split("; ").reduce((acc, cookie) => {
      const [key, value] = cookie.split("=");
      acc[key] = value;
      return acc;
    }, {});

    setIsLoggedIn(!!cookies.accessToken); // ✅ accessToken이 있으면 로그인 상태로 설정
  };

  // useEffect에서 로그인 상태를 지속적으로 확인
  useEffect(() => {
    checkLoginStatus(); // 초기 확인

    // ✅ 쿠키 변경 감지를 위해 setInterval 사용
    const interval = setInterval(() => {
      checkLoginStatus();
    }, 1000); // 1초마다 체크

    return () => clearInterval(interval); // 언마운트 시 정리
  }, []);
  
  const handleLogout = async () => {
    await logoutUser();
    setIsLoggedIn(false); // ✅ 상태 업데이트
    window.location.href = "/login"; // ✅ 로그아웃 후 로그인 페이지로 이동
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>
      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>

        {/* ✅ 로그인 상태에 따라 버튼 변경 */}
        {isLoggedIn ? (
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        ) : (
          <Link to="/login" className="login-btn">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
