import React, { useState, useEffect } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket"; // ✅ WebSocket 클라이언트 가져오기
import "../styles/Chatbox.css"; 

const Chatbox = ({ projectId }) => { // ✅ 프로젝트 ID를 props로 받음
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // ✅ 채팅 메시지 불러오기 함수
  const fetchChatMessages = async () => {
    if (!projectId) {
      console.warn("❌ 프로젝트 ID가 정의되지 않음, 채팅 불러오지 않음.");
      return;
    }

    const token = getAccessToken();
    try {
      const response = await axios.get(`http://localhost:8082/api/chat/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
      console.log("✅ 채팅 메시지 로드 성공:", response.data);
    } catch (error) {
      console.error("❌ 채팅 기록 불러오기 실패:", error);
    }
  };

  // ✅ 프로젝트가 변경될 때 WebSocket 구독 및 기존 메시지 불러오기
  useEffect(() => {
    if (!projectId) return;

    console.log(`🔄 Chatbox에서 감지된 프로젝트 ID: ${projectId}`);
    fetchChatMessages(); // ✅ 채팅 기록 가져오기

    const stompClient = getStompClient();

    if (!stompClient.connected) {
      console.warn("⚠️ WebSocket이 아직 연결되지 않음. 활성화 시도...");
      stompClient.activate();
    }

    // ✅ 구독 (Subscribe) 설정
    const subscription = stompClient.subscribe(`/topic/chat/${projectId}`, (message) => {
      const receivedMessage = JSON.parse(message.body);
      console.log("📩 새 채팅 메시지 수신:", receivedMessage);
      setMessages((prevMessages) => [...prevMessages, receivedMessage]); // ✅ 메시지 추가
    });

    return () => {
      console.log("🛑 WebSocket 구독 해제:", `/topic/chat/${projectId}`);
      subscription.unsubscribe();
    };
  }, [projectId]);

  // ✅ 메시지 전송 함수
  const sendMessage = async () => {
    if (!newMessage.trim() || !projectId) return;

    const token = getAccessToken();
    try {
      const messageData = {
        content: newMessage.trim(),
        sender: { email: localStorage.getItem("userEmail") }, // ✅ 현재 로그인한 사용자 이메일 추가
      };

      // ✅ WebSocket을 통해 메시지 전송
      const stompClient = getStompClient();
      stompClient.publish({
        destination: `/app/chat/${projectId}`,
        body: JSON.stringify(messageData),
      });

      console.log("📤 메시지 전송:", messageData.content);
      setNewMessage(""); // 입력 필드 초기화
    } catch (error) {
      console.error("❌ 메시지 전송 실패:", error);
    }
  };

  return (
    <div className="chatbox-container">
      <h3>팀 채팅</h3>
      {projectId ? (
        <>
          <div className="messages">
            {messages.length > 0 ? (
              messages.map((msg, index) => (
                <p key={index}>
                  <strong>{msg.sender.username}</strong>: {msg.content}
                </p>
              ))
            ) : (
              <p>📭 아직 메시지가 없습니다.</p>
            )}
          </div>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="메시지를 입력하세요..."
          />
          <button onClick={sendMessage}>전송</button>
        </>
      ) : (
        <p>🚨 프로젝트를 선택해주세요.</p> // ✅ 프로젝트가 없을 때 안내 메시지
      )}
    </div>
  );
};

export default Chatbox;
