import React, { useState } from "react";
import "../styles/TaskModal.css";

const TaskModal = ({ onClose }) => {
  // ✅ 입력값을 저장할 상태 정의
  const [taskData, setTaskData] = useState({
    assignedTo: "", // 담당자
    startDate: "", // 작업 시작일
    dueDate: "", // 작업 마감일
    description: "", // 작업 내용
  });

  // ✅ 입력값이 변경될 때 상태 업데이트
  const handleChange = (e) => {
    setTaskData({ ...taskData, [e.target.name]: e.target.value });
  };

  // ✅ "등록" 버튼 클릭 시 콘솔에 입력값 출력 (백엔드 연동 전 테스트)
  const handleSubmit = () => {
    console.log("📌 입력된 업무 정보:", taskData);
    onClose(); // 등록 후 모달 닫기
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>업무 추가</h2>

        {/* ✅ 담당자 선택 */}
        <label>담당자:</label>
        <select name="assignedTo" value={taskData.assignedTo} onChange={handleChange}>
          <option value="">담당자 선택</option>
          <option value="user1">사용자 1</option> {/* ⚠️ 나중에 팀원 목록으로 대체 */}
          <option value="user2">사용자 2</option>
        </select>

        {/* ✅ 작업 시작일 선택 */}
        <label>작업 시작일:</label>
        <input type="date" name="startDate" value={taskData.startDate} onChange={handleChange} />

        {/* ✅ 작업 마감일 선택 */}
        <label>작업 마감일:</label>
        <input type="date" name="dueDate" value={taskData.dueDate} onChange={handleChange} />

        {/* ✅ 작업 내용 입력 */}
        <label>작업 내용:</label>
        <textarea name="description" value={taskData.description} onChange={handleChange} />

        {/* ✅ 버튼 영역 */}
        <div className="modal-actions">
          <button onClick={onClose}>취소</button>
          <button onClick={handleSubmit} disabled={!taskData.assignedTo || !taskData.startDate || !taskData.dueDate || !taskData.description}>
            등록
          </button>
        </div>
      </div>
    </div>
  );
};

export default TaskModal;
