import React, { useEffect, useState } from "react";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket";
import axios from "axios";
import "../styles/Chatbox.css";

const Chatbox = ({ projectId }) => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const stompClient = getStompClient();
  const user = JSON.parse(localStorage.getItem("user")); // ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥

  // ‚úÖ Ï±ÑÌåÖ Í∏∞Î°ù Î∂àÎü¨Ïò§Í∏∞
  const fetchChatMessages = async () => {
    try {
      const token = getAccessToken();
      const response = await axios.get(`http://localhost:8082/api/chat/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
    } catch (error) {
      console.error("‚ùå Ï±ÑÌåÖ Í∏∞Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  useEffect(() => {
    fetchChatMessages();

    // ‚úÖ WebSocketÏù¥ Ïó∞Í≤∞Îêú ÌõÑÏóêÎßå Íµ¨ÎèÖ Ïã§Ìñâ
    if (!stompClient.connected) {
      console.log("üîÑ WebSocket Ïó∞Í≤∞ ÌôúÏÑ±Ìôî ÏãúÎèÑ...");
      stompClient.activate();
    }

    // ‚úÖ WebSocket Ïó∞Í≤∞Ïù¥ ÏôÑÎ£åÎêú ÌõÑ Íµ¨ÎèÖ
    stompClient.onConnect = () => {
      console.log("‚úÖ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ, Ï±ÑÌåÖ Íµ¨ÎèÖ ÏãúÏûë!");
      stompClient.subscribe(`/topic/chat/${projectId}`, (message) => {
        const newMessage = JSON.parse(message.body);
        setMessages((prevMessages) => [...prevMessages, newMessage]);
      });
    };

    return () => {
      console.log("üõë WebSocket Ïó∞Í≤∞ Ìï¥Ï†ú");
      if (stompClient && stompClient.connected) {
        stompClient.deactivate();
      }
    };
  }, [projectId]);

  // ‚úÖ Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const sendMessage = () => {
    if (message.trim() === "" || !stompClient.connected) return; // ‚úÖ Ïó∞Í≤∞ÎêòÏßÄ ÏïäÏïòÏúºÎ©¥ Ï†ÑÏÜ° X

    const token = getAccessToken();
    const chatMessage = {
      sender: { email: user.email },
      content: message,
    };

    stompClient.publish({
      destination: `/app/chat/${projectId}`,
      body: JSON.stringify(chatMessage),
      headers: { Authorization: `Bearer ${token}` },
    });

    setMessage(""); // ÏûÖÎ†• ÌïÑÎìú Ï¥àÍ∏∞Ìôî
  };

  return (
    <div className="chatbox">
      <h3>üì¢ ÌîÑÎ°úÏ†ùÌä∏ Ï±ÑÌåÖ</h3>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-message ${msg.sender.email === user.email ? "mine" : ""}`}>
            <strong>{msg.sender.email}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <button onClick={sendMessage}>Ï†ÑÏÜ°</button>
    </div>
  );
};

export default Chatbox;
