import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { stompClient } from "../api/websocket";
import "../styles/InviteList.css";

const InviteList = ({ refreshProjects }) => {
    const [invites, setInvites] = useState([]);
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        const fetchInvites = async () => {
            const token = getAccessToken();
            try {
                const response = await axios.get("http://localhost:8082/api/team/invites", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log("üìå Î∞õÏùÄ Ï¥àÎåÄ Î™©Î°ù:", response.data);
                setInvites(response.data);
                setNotifications(response.data.filter(invite => invite.status === "PENDING")); // üöÄ PENDING ÏÉÅÌÉúÎßå ÏïåÎ¶º ÌëúÏãú
            } catch (error) {
                console.error("‚ùå Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
            }
        };

        fetchInvites();

        stompClient.onConnect = (frame) => {
            console.log("‚úÖ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ:", frame);
            stompClient.subscribe("/topic/invites", (message) => {
                console.log("üì© ÏÉà Ï¥àÎåÄ ÏïåÎ¶º ÏàòÏã†:", message.body);
                const newInvite = JSON.parse(message.body);

                // üöÄ Ï¥àÎåÄ ÏÉÅÌÉúÍ∞Ä PENDINGÏùº Í≤ΩÏö∞Îßå Ï∂îÍ∞Ä
                if (newInvite.status === "PENDING") {
                    setNotifications((prev) => {
                        console.log("üîî Í∏∞Ï°¥ ÏïåÎ¶º:", prev);
                        return [...prev, newInvite];
                    });
                }
            });
        };

        stompClient.activate();

        return () => {
            stompClient.deactivate();
        };
    }, []);

    return (
        <>
            {/* ‚úÖ Ï¥àÎåÄ ÏïåÎ¶º ÌåùÏóÖ UI */}
            <div className="invite-popup-container">
                {notifications.length > 0 ? (
                    notifications.map((invite) => (
                        <div key={invite.id} className="invite-popup">
                            <p>
                                <strong>{invite.sender.username}</strong> ÎãòÏù¥ <strong>{invite.project.name}</strong> ÌîÑÎ°úÏ†ùÌä∏Ïóê Ï¥àÎåÄÌñàÏäµÎãàÎã§.
                            </p>
                            <button onClick={() => setNotifications(notifications.filter(n => n.id !== invite.id))}>
                                ‚úÖ ÌôïÏù∏
                            </button>
                        </div>
                    ))
                ) : (
                    <p className="no-invites">üìå ÏÉàÎ°úÏö¥ Ï¥àÎåÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
                )}
            </div>
        </>
    );
};

export default InviteList;
